import { makeAutoObservable } from 'mobx';
import { Square } from './square'

/*class TodoList {
    todos = []
    get unfinishedTodoCount() {
        return this.todos.filter(todo => !todo.finished).length
    }
    constructor(todos) {
        makeObservable(this, {
            todos: observable,
            unfinishedTodoCount: computed
        })
        this.todos = todos
    }
}*/

export class Grid {
    squares = [];

    testGrid(size = 30) {
        let grid = []
        let n = 0
        for (let i = 0; i < size; i++) {
            grid.push([])
            for (let j = 0; j < size; j++) {
                grid[i].push(new Square(n));
                n = n + 1;
            }
        }
        this.squares = grid
    }

    constructor() {
        makeAutoObservable(this);
    }

    get getGrid() {
        return this.squares;
    }

    makeGrid() {
        //
    }

    getSquare(x, y) {
        return this.squares[x][y];
    }
}
