import { action, computed, makeAutoObservable, observable } from 'mobx';
import { Node } from '../components/node';

export class GridStore {
    squares = [];
    drawingMode = 0;  // 0: empty, 1: wall, 2: start, 3: end
    startNode = {
        x: 10,
        y: 10
    }
    endNode = {
        x: 80,
        y: 35
    }

    constructor(width, height) {
        makeAutoObservable(this, {
            squares: observable,
            drawingMode: observable,
            startNode: observable.struct,
            endNode: observable.struct,
            initiateGrid: action,
            initiateEndPoints: action,
            changeGrid: action,
            clearGrid: action,
            setDrawingMode: action,
            isStartOrEnd: action
        });
        this.setDrawingMode(1);
        this.initiateGrid(width, height);
    }

    initiateGrid(width, height) {
        const grid = []
        for (let i = 0; i < height; i++) {
            grid.push([])
            for (let j = 0; j < width; j++) {
                if (i == this.startNode.y && j == this.startNode.x) grid[i].push(new Node(j, i, 2));
                else if (i == this.endNode.y && j == this.endNode.x) grid[i].push(new Node(j, i, 3));
                else grid[i].push(new Node(j, i));
            }
        }
        this.squares = grid;
    }

    isStartOrEnd(node) {
        if (node.nodeType === 2 || node.nodeType === 3) return true;
        else return false;
    }

    changeGrid(node, mode) {
        switch (mode) {
            case 0:
                if (!this.isStartOrEnd(node)) node.changeType(0);
                break;
            case 1:
                if (!this.isStartOrEnd(node)) node.changeType(1);
                break;
            case 2:
                node.changeType(2);
                break;
            case 3:
                node.changeType(3);
                break;
        }
    }

    clearGrid() {
        for (let row = 0; row < this.squares.length; row++) {
            for (let col = 0; col < this.squares[row].length; col++) {
                if (!this.isStartOrEnd(this.squares[row][col])) this.squares[row][col].resetType();
            }
        }
    }

    setDrawingMode(mode) {
        this.drawingMode = mode;
    }
}
export default GridStore
