import { action, makeAutoObservable, observable } from 'mobx';
import { Node } from '../util/node';

export class Grid {
    squares = [];

    constructor(width, height) {
        makeAutoObservable(this, {
            squares: observable,
            initiateGrid: action,
            changeNode: action
        });
        this.initiateGrid(width, height);
    }

    initiateGrid(width, height) {
        let grid = []
        for (let i = 0; i < height; i++) {
            grid.push([])
            for (let j = 0; j < width; j++) {
                grid[i].push(new Node(j, i));
            }
        }
        this.squares = grid
    }

    changeNode(x, y) {
        const target = document.getElementById('node-' + x + '-' + y);
        target.className = 'node-start';
    }

    testGrid(size = 20) {
        let grid = []
        let n = 0
        for (let i = 0; i < size; i++) {
            grid.push([])
            for (let j = 0; j < size; j++) {
                grid[i].push(new Node(n));
                n = n + 1;
            }
        }
        this.squares = grid
    }

    testElementById(x = 10, y = 10) {
        const temp = document.getElementById('node-' + x + '-' + y);
        temp.className = 'node-start';
    }
}
