import { makeAutoObservable, observable, action } from "mobx"

export class MouseEventStore {
    isMouseDown = false;

    constructor() {
        makeAutoObservable(this, { 
            isMouseDown: observable, 
            mouseDown: action,
            mouseUp: action,
            mouseOver: action
        });
    }

    mouseDown(x, y, mode, event) {
        this.isMouseDown = true;
        event(x, y, mode);
    }

    mouseUp() {
        this.isMouseDown = false;
    }

    mouseOver(x, y, mode, event) {
        if (this.isMouseDown) {
            event(x, y, mode);
        }
    }

}
