import { makeAutoObservable } from 'mobx';
import { WindowSizeUtil } from '../helper/windowSizeUtil';
import { SquareStore } from './squareStore';

/*class TodoList {
    todos = []
    get unfinishedTodoCount() {
        return this.todos.filter(todo => !todo.finished).length
    }
    constructor(todos) {
        makeObservable(this, {
            todos: observable,
            unfinishedTodoCount: computed
        })
        this.todos = todos
    }
}*/

export class Grid {
    squares = [];

    constructor(width, height) {
        makeAutoObservable(this);
        const { iwidth, iheight } = WindowSizeUtil();
        console.log(iwidth + " + " + iheight);
        this.initiateGrid(width, height);
    }

    initiateGrid(width, height) {
        let grid = []
        for (let i = 0; i < height; i++) {
            grid.push([])
            for (let j = 0; j < width; j++) {
                grid[i].push(new SquareStore(0));
            }
        }
        this.squares = grid
    }

    get getGrid() {
        return this.squares;
    }

    testGrid(size = 20) {
        let grid = []
        let n = 0
        for (let i = 0; i < size; i++) {
            grid.push([])
            for (let j = 0; j < size; j++) {
                grid[i].push(new SquareStore(n));
                n = n + 1;
            }
        }
        this.squares = grid
    }
}
