import { action, computed, makeAutoObservable, observable } from 'mobx';
import { Node } from '../components/node';

export class GridStore {
    /*squares = [];
    drawingMode = 0;*/   // 0: empty, 1: wall, 2: start, 3: end

    constructor(width, height) {
        makeAutoObservable(this, {
            squares: observable,
            drawingMode: observable,
            initiateGrid: action,
            changeGrid: action,
            clearGrid: action,
            setDrawingMode: action
        });
        this.setDrawingMode(1);
        this.initiateGrid(width, height);
    }

    initiateGrid(width, height) {
        let grid = []
        for (let i = 0; i < height; i++) {
            grid.push([])
            for (let j = 0; j < width; j++) {
                grid[i].push(new Node(j, i));
            }
        }
        this.squares = grid
    }

    changeGrid(x, y) {
        const target = document.getElementById('node-' + x + '-' + y);
        switch (this.drawingMode) {
            case 0:
                target.className = 'node';
                break;
            case 1:
                target.className = 'node-wall';
                break;
            case 2:
                target.className = 'node-start';
                break;
            case 3:
                target.className = 'node-end';
                break;
        }
    }

    clearGrid() {
        for (let row = 0; row < this.squares.length; row++) {
            for (let col = 0; col < this.squares[row].length; col++) {
                this.squares[row][col].resetType();
                const target = document.getElementById('node-' + col + '-' + row);
                target.className = 'node';
            }
        }
    }

    setDrawingMode(mode) {
        this.drawingMode = mode;
    }
}
export default GridStore
