import { makeAutoObservable, observable, action } from "mobx"

export class MouseEventStore {
    isMouseDown = false;

    constructor() {
        makeAutoObservable(this, { 
            isClickOnly: observable,
            isMouseDown: observable, 
            mouseClick: action,
            mouseDown: action,
            mouseUp: action,
            mouseOver: action
        });
    }

    mouseClick(x, y, mode, event) {
        if (mode == 0 || mode == 1) return;
        event(x, y, mode);
    }

    mouseDown(x, y, mode, event) {
        if (mode == 2 || mode == 3) return;
        this.isMouseDown = true;
        event(x, y, mode);
    }

    mouseUp() {
        this.isMouseDown = false;
    }

    mouseOver(x, y, mode, event) {
        if (mode == 2 || mode == 3) return;
        if (this.isMouseDown) {
            event(x, y, mode);
        }
    }

}
